import { createApp } from 'vue'
import App from './App.vue'
import router from './router'

import 'font-awesome/css/font-awesome.min.css'
import axios from 'axios'
import qs from 'qs'
// import {
// 	getCurDate,
// 	setSessionStorage,
// 	getSessionStorage,
// 	removeSessionStorage,
// 	setLocalStorage,
// 	getLocalStorage,
// 	removeLocalStorage
// } from './common.js'

// //设置axios的基础url部分
// axios.defaults.baseURL = 'http://localhost:8081/elm/';
// //将axios挂载到vue实例上，使用时就可以this.$axios这样使用了
// Vue.prototype.$axios = axios;

// Vue.prototype.$getCurDate = getCurDate;
// Vue.prototype.$setSessionStorage = setSessionStorage;
// Vue.prototype.$getSessionStorage = getSessionStorage;
// Vue.prototype.$removeSessionStorage = removeSessionStorage;
// Vue.prototype.$setLocalStorage = setLocalStorage;
// Vue.prototype.$getLocalStorage = getLocalStorage;
// Vue.prototype.$removeLocalStorage = removeLocalStorage;

router.beforeEach(function(to, from, next){
	let user = this.sessionStorage.getItem('user');
	//除了登录、注册、首页、商家列表、商家信息之外，都需要判断是否登录
	if(!(to.path=='/'||to.path=='/index'||to.path=='/businessList'||to.path=='/businessInfo'||to.path=='/login'||to.path=='/register')){
		if(user==null){
			router.push('/login');
			location.reload;
		}
	}
	next();
});

createApp(App).use(router).mount('#app')
